package com.phoenix.ecom.controller;

import com.phoenix.ecom.repository.image.IImageRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpHeaders;
import org.springframework.http.HttpStatus;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.*;

@RestController
@RequestMapping("/api/v1")
public class ImageController {

    @Autowired
    IImageRepository imageRepository;


    @GetMapping(value = "/image/{id}", produces = MediaType.IMAGE_JPEG_VALUE)
    public ResponseEntity<byte[]> getImageAsResponseEntity(@PathVariable String id) {
        HttpHeaders headers = new HttpHeaders();

        byte[] bt = getImageByte(id);

        return new ResponseEntity<byte[]>(bt, HttpStatus.OK);
    }

    private byte[] getImageByte(String id) {
        //String data = "data:image/jpeg;base64,";

        String data = imageRepository.getImage(id);
        String base64Image = data.split(",")[1];
        byte[] imageBytes = javax.xml.bind.DatatypeConverter.parseBase64Binary(base64Image);
        return imageBytes;
    }
}
